import React from "react";

function Compare(){
    const [movie1, setMovie1] = useState('');
    const [movie2, setMovie2] = useState('');
    const [movieData1, setMovieData1] = useState({});
    const [movieData2, setMovieData2] = useState({});

    const fetchData = async () => {
        if (movie1 && movie2) {
        const response1 = await axios.get(`http://www.omdbapi.com/?t=${movie1}&apikey=4932ab16`);
        const response2 = await axios.get(`http://www.omdbapi.com/?t=${movie2}&apikey=4932ab16`);

        setMovieData1(response1.data);
        setMovieData2(response2.data);
        }
    };

    const data1 = {
        labels: ['Rating', 'Metascore', 'Value'],
        datasets: [
        {
            label: movie1.Title,
            data: [movieData1.imdbRating, movieData1.Metascore, movieData1.imdbVotes],
            backgroundColor: ['rgba(255, 99, 132, 0.2)'],
            borderColor: ['rgba(255, 99, 132, 1)'],
            borderWidth: 1,
        },
        ],
    };

    const data2 = {
        labels: ['Rating', 'Metascore', 'Value'],
        datasets: [
        {
            label: movie2.Title,
            data: [movieData2.imdbRating, movieData2.Metascore, movieData2.imdbVotes],
            backgroundColor: ['rgba(54, 162, 235, 0.2)'],
            borderColor: ['rgba(54, 162, 235, 1)'],
            borderWidth: 1,
        },
        ],
    };

    const radarData = {
        labels: ['Rating', 'Metascore', 'Value'],
        datasets: [
        {
            label: movie1.Title,
            data: [movieData1.imdbRating, movieData1.Metascore, movieData1.imdbVotes],
            backgroundColor: 'rgba(255, 99, 132, 0.2)',
            borderColor: 'rgba(255, 99, 132, 1)',
            pointBackgroundColor: 'rgba(255, 99, 132, 1)',
        },
        {
            label: movie2.Title,
            data: [movieData2.imdbRating, movieData2.Metascore, movieData2.imdbVotes],
            backgroundColor: 'rgba(54, 162, 235, 0.2)',
            borderColor: 'rgba(54, 162, 235, 1)',
            pointBackgroundColor: 'rgba(54, 162, 235, 1)',
        },
        ],
    };

    return (
        <div className="container">
        <div className="chart-container">
            <h2>{movie1.Title}</h2>
            <input type="text" placeholder="Enter Movie 1" value={movie1} onChange={(e) => setMovie1(e.target.value)} />
            <button onClick={fetchData}>Search</button>
            <div className="chart">
            <h3>Bar Chart</h3>
            <Bar data={data1} />
            </div>
            <div className="chart">
            <h3>Pie Chart</h3>
            <Pie data={data1} />
            </div>
        </div>

        <div className="chart-container">
            <h2>{movie2.Title}</h2>
            <input type="text" placeholder="Enter Movie 2" value={movie2} onChange={(e) => setMovie2(e.target.value)} />
            <button onClick={fetchData}>Search</button>
            <div className="chart">
            <h3>Bar Chart</h3>
            <Bar data={data2} />
            </div>
            <div className="chart">
            <h3>Pie Chart</h3>
            <Pie data={data2} />
            </div>
        </div>

        <div className="radar-chart-container">
            <h3>Radar Chart</h3>
            <Radar data={radarData} />
        </div>
        </div>
    );
    };

export default Compare;
